import React, {Fragment, useEffect, useState} from "react";
const ListTeachers = () => {
    const [teachers, setTeachers] = useState([])
    const getTeachers = async ()=> {
        try {
            const response = await fetch(" http://localhost:4000/teacher", {
                method: "GET",
            })
            const jsonData = await response.json()

            setTeachers(jsonData) 


        } catch (error) {
            console.log(error.message)
        }
    }

    useEffect(() => {
        getTeachers()
    })

    const dropDown =(e) => {
        const teacher = JSON.parse(e.target.value)
        console.log("teacher")
    }

    return ( 
        <Fragment>
            <table className="table">
    <thead>
      <tr>
        <th>Firstname</th>
        <th>Lastname</th>
        <th>Email</th>
      </tr>
    </thead>
    <tbody>

      {teachers.map(teacher =>(
        <tr>
        <td>{teacher.first_name}</td>
        <td>{teacher.last_name}</td>
        <td>{ teacher.email }</td>
        <td>delete</td>
        <td>edit</td>
      </tr>
      ))}
    </tbody>
  </table>
        <h1>Select your course teacher</h1>
        <select onChange={dropDown}>
            {teachers.map(teacher => (
                <option
                value={JSON.stringify(teacher)}>{teacher.email}</option>
            ))}
        </select>


        </Fragment>

     );
}

export default ListTeachers
